num_ronum_dict = {    1000: 'M', 900: 'CM', 500: 'D', 400: 'CD', 100: 'C', 90: 'XC',      50: 'L',  40: 'XL',  10: 'X',   9: 'IX',   5: 'V',  4: 'IV', 1: 'I'    }def int2ronum(inputNumber):    if not inputNumber:        return 'N'    result = ''    for number, ronum in num_ronum_dict.items():        thousand = int(inputNumber / 1000)        if thousand:            result = 'M' * thousand            try:                inputNumber -= 1000 * thousand            except:                inputNumber = 100000        while inputNumber >= number:            result = result + ronum            inputNumber -= number    return resultdef ronum2int(inputString):    inputString = inputString.upper()    if inputString == 'N':        return 0    result = 0    if inputString.count('M'):        result = 1000 * inputString.count('M')        inputString = inputString.rsplit('M', 1)[1]    for number, ronum in num_ronum_dict.items():        while inputString.startswith(ronum):            result += number            inputString = inputString.split(ronum, 1)[1]    return result